import React, { useState, useEffect } from 'react';
import Layout from '../../components/Layout/Layout';
import { toast } from 'react-toastify';
import axios from 'axios';
import { Redirect } from 'react-router-dom';

const Login = () => {
    // State variables for email, password, and redirectToHome
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [redirectToHome, setRedirectToHome] = useState(false);

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const res = await axios.post(`${process.env.REACT_APP_API}/api/v1/auth/login`, { email, password });
            if (res.data.success) {
                toast.success(res.data.message);
                setRedirectToHome(true); // Set the redirect flag to true upon successful login
            } else {
                toast.error(res.data.message);
            }
        } catch (error) {
            console.error(error);
            toast.error('Something went wrong');
        }
    };

    // Use useEffect to trigger the redirect when redirectToHome becomes true
    useEffect(() => {
        if (redirectToHome) {
            // Redirect to the home page ("/")
            // You can also use a different route path if needed
            return <Redirect to="/" />;
        }
    }, [redirectToHome]);

    return (
        <Layout title={"Login-Ecommerce"}>
            <div className="register">
                <h1>Login</h1>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="email" className="form-label">Email</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="form-control"
                            id="email"
                            placeholder='Enter your email'
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">Password</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="form-control"
                            id="password"
                            placeholder='Enter your password'
                            required
                        />
                    </div>
                    {/* Submit button for the login form */}
                    <button type="submit" className="btn btn-primary">Login</button>
                </form>
            </div>
        </Layout>
    );
};

export default Login;
